import sysif sys.argv[1] == '-e': # encode    encode = Trueelif sys.argv[1] == '-d': # decode    encode = False    keyIn = sys.argv[2].lower() # lowercase to make shifting easierif(keyIn.isalpha()): # if key only has letters, take as is    key = keyInelse: # otherwise there are special characters    key = ''    for char in keyIn: # take only the letters, leave out other characters        if(char.isalpha()):            key = key + charfor line in sys.stdin:    textIn = line.strip() # read input           output = ""    i = 0 # index for keys    if encode:          for char in textIn:            if(char.isupper()): # uppercase letters. ASCII between 65 and 90                # shifts characters to fall between 0-25, adds key to text value,                 # mod 26 for the correct letter, then shifts back to ASCII                 output += chr( ( ord(char)-65 + ord(key[i%len(key)])-97 )%26 + 65 )                i = i+1            elif(char.islower()): # lowercase letters. Between 97 and 122                output += chr( ( ord(char)-97 + ord(key[i%len(key)])-97 )%26 + 97 )                i = i+1            else: # otherwise it's a special character, leave as is                output += char    else:        for char in textIn:            if(char.isupper()): # uppercase letters. ASCII between 65 and 90                # similar to above, but add 26 to make sure it stays positive and subtract key                output += chr( ( 26 + ord(char)-65 - (ord(key[i%len(key)])-97) )%26 + 65 )                i = i+1            elif(char.islower()): # lowercase letters. Between 97 and 122                output += chr( ( 26 + ord(char)-97 - (ord(key[i%len(key)])-97) )%26 + 97 )                i = i+1            else: # otherwise it's a special character, leave as is                output += char    print(output)